mapper.update:
UPDATE mkl_store_demand
examine_time = #{examine_time}, examine = #{examine}, examine_two_phone = #{userName}, examine_two_name = #{realName},
examine_reason = #{examine_reason}
WHERE id in
#{item}

mapper.select:
SELECT store_shopowner_phone FROM mkl_store_demand WHERE id in
#{item}
GROUP BY store_shopowner_phone
