mapper.select:
SELECT * FROM mkl_mail_info mmi AND isDelete=0 AND mmi.courierPhone is NOT NULL
and (state=0 or state=1) and state=4 and (state=5 or state=6 ) and (state=2 or state=3 )
ORDER BY mmi.shipTime DESC limit #{start}, #{size}

mapper.update:
UPDATE mkl_mail_info SET 
isDelete = '1'
WHERE id = #{id};

mapper.select:
select count(1) from mkl_store_info msi
where 1=1
and msi.channel_code = #{channel_code}

SELECT MIN(C.num), C.accountName as phone from ( SELECT A.accountName, IFNULL(B.con,0) as 'num' FROM ( SELECT * FROM sys_account WHERE accountName in
#{item}
) A LEFT JOIN ( SELECT COUNT(courierPhone) AS con, courierPhone FROM mkl_mail_info WHERE courierPhone in('13545675856', '13523698563') ) B ON A.accountName = B.courierPhone where 1=1 GROUP BY A.accountName )C
